import axios from 'axios';
import { CREATE_TODO, DELETE_TODO, LOGIN, REGISTER, TODO_LIST } from './apiConstant.js';

export const login = async (data) => {
    return axios.post(LOGIN , data )
}

export const register = async (data) => {
    return axios.post(REGISTER , data )
}


export const creataTodoApi = async (data) => {
    let token =getToken();
    console.log(token , 'token')

    return axios.post(CREATE_TODO , data , {
        headers:{
            auth:token
        }
    })
}

export const getTodoListApi = async (data) => {
    let token = getToken();
    try {
        return await axios.get(TODO_LIST, {
            headers: {
                Auth: `Bearer ${token}`
            },
            params: data
        });
    } catch (error) {
        console.error('API call failed:', error);
        return error.response; // This makes sure that you handle the error and possibly return it
    }
};


export const deleteTodoApi = async (data) => {
    let token = getToken();
    console.log(token , 'token')

    return axios.post(DELETE_TODO , data , {
        headers:{
            auth:token
        }
    })
}


export function getToken() {
    let user = localStorage.getItem('user');
    if(!user) throw "user not present"
    const userObj = JSON.parse(user);
    return userObj.token;
}
